import React, { useEffect, useState } from "react";
import axios from "axios";
import Select from "react-select";
import PoseImage from "../components/PoseImage";

const Poses = () => {
  const [categories, setCategories] = useState([]);
  const [poses, setPoses] = useState([]);
  const [filteredPoses, setFilteredPoses] = useState([]);

  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedPose, setSelectedPose] = useState(null);

  // Load categories on page load
  useEffect(() => {
    axios.get("http://localhost:8000/api/categories/")
      .then(res => setCategories(res.data))
      .catch(err => console.error(err));
  }, []);

  // Load poses when category selected
  const handleCategoryChange = (categoryId) => {
    setSelectedCategory(categoryId);
    setSelectedPose(null);

    axios.get(`http://localhost:8000/api/poses/category/${categoryId}/`)
      .then(res => setFilteredPoses(res.data))
      .catch(err => console.error(err));
  };

  // react-select options
  const poseOptions = filteredPoses.map(pose => ({
    value: pose.id,
    label: pose.name,
    pose: pose
  }));

  return (
    <div>
      <h2>Select Yoga Pose</h2>

      {/* CATEGORY SELECT */}
      <select
        onChange={(e) => handleCategoryChange(e.target.value)}
        value={selectedCategory}
      >
        <option value="">Select Category</option>
        {categories.map(cat => (
          <option key={cat.id} value={cat.id}>
            {cat.name}
          </option>
        ))}
      </select>

      {/* POSE SEARCH COMBO (appears after category selection) */}
      {filteredPoses.length > 0 && (
        <div style={{ marginTop: "15px", width: "300px" }}>
          <Select
            options={poseOptions}
            placeholder="Type to search yoga pose..."
            onChange={(option) => setSelectedPose(option.pose)}
            isClearable
          />
        </div>
      )}

      {/* POSE DETAILS */}
      {selectedPose && (
        <div style={{ marginTop: "20px" }}>
          <h3>{selectedPose.name}</h3>
          <p><b>Category:</b> {selectedPose.category}</p>

          <PoseImage
            src={`/images/${selectedPose.image}`}
            alt={selectedPose.name}
            width="300px"
          />
        </div>
      )}
    </div>
  );
};

export default Poses;
