import React, { useEffect, useRef } from "react";
import "./Home.css";
import profileImg from "../assets/rajan.jpg";
import omSound from "../assets/om.mp3"; // add a soft OM sound file here

const Home = () => {
  const audioRef = useRef(null);

  useEffect(() => {
    // Play OM sound gently after user interaction-safe delay
    const timer = setTimeout(() => {
      if (audioRef.current) {
        audioRef.current.volume = 0.3;
        audioRef.current.play().catch(() => {});
      }
    }, 1200);

    return () => clearTimeout(timer);
  }, []);

  return (
    <section className="home-section">
      <audio ref={audioRef} src={omSound} preload="auto" />

      <div className="home-wrapper">

        {/* LEFT : SURYA NAMASKAR + PHOTO */}
        <div className="home-left">

          {/* Surya Namaskar SVG */}
          <div className="surya-container">
            <svg
              viewBox="0 0 300 300"
              className="surya-svg"
              aria-label="Surya Namaskar Animation"
            >
              <circle cx="150" cy="150" r="120" className="sun-circle" />

              {Array.from({ length: 12 }).map((_, i) => (
                <g
                  key={i}
                  className={`asana-step step-${i + 1}`}
                  transform={`rotate(${i * 30} 150 150) translate(0 -95)`}
                >
                  <circle cx="150" cy="150" r="8" />
                  <line
                    x1="150"
                    y1="158"
                    x2="150"
                    y2="185"
                    strokeWidth="3"
                  />
                  <line
                    x1="150"
                    y1="165"
                    x2="138"
                    y2="175"
                    strokeWidth="3"
                  />
                  <line
                    x1="150"
                    y1="165"
                    x2="162"
                    y2="175"
                    strokeWidth="3"
                  />
                </g>
              ))}
            </svg>
          </div>

          {/* Profile Photo */}
          <img
            src={profileImg}
            alt="Rajendrakumar R. Nair - YogaPlanet"
            className="profile-photo"
          />
        </div>

        {/* RIGHT : TEXT */}
        <div className="home-text">
          <h1>Rajendrakumar R. Nair</h1>
          <h2>BSc Chemistry · MSc Yoga Therapy</h2>

          <p className="certification">
            Certified International Yoga Teacher (Yoga Alliance)
          </p>

          <p className="experience">
            40+ Years of Experience in Traditional Hatha Yoga & Meditation
          </p>

          <p>
            I am a lifelong practitioner and teacher of Yoga, rooted in the
            classical yogic tradition and enriched by scientific understanding.
            My mission is to guide individuals toward balance, awareness, and
            inner harmony.
          </p>

          <p>
            <strong>YogaPlanet</strong> is a global initiative dedicated to
            spreading the timeless wisdom of Yoga worldwide—bridging ancient
            yogic science with modern technology.
          </p>

          <p className="quote">
            “Let Yoga unite humanity.  
            Let peace begin within.”
          </p>
        </div>

      </div>
    </section>
  );
};

export default Home;
