import React, { useState, useEffect } from "react";

export default function PoseFilter({
  categories = [],
  posesByCategory = {},
  onSelectPose = () => {}
}) {
  const [category, setCategory] = useState("");
  const [search, setSearch] = useState("");
  const [poses, setPoses] = useState([]);

  useEffect(() => {
    if (category && posesByCategory[category]) {
      setPoses(posesByCategory[category]);
    } else {
      setPoses([]);
    }
    setSearch("");
  }, [category, posesByCategory]);

  const filtered = poses.filter(p =>
    p?.name?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ maxWidth: "400px" }}>
      <label>Category</label><br />
      <select value={category} onChange={e => setCategory(e.target.value)}>
        <option value="">-- Select Category --</option>
        {categories.map(c => (
          <option key={c} value={c}>{c}</option>
        ))}
      </select>

      {category && (
        <>
          <br /><br />
          <label>Search Pose</label><br />
          <input
            type="text"
            placeholder="Type to filter pose..."
            value={search}
            onChange={e => setSearch(e.target.value)}
            style={{ width: "100%" }}
          />

          <ul style={{
            maxHeight: "160px",
            overflowY: "auto",
            border: "1px solid #ccc",
            padding: "5px",
            listStyle: "none"
          }}>
            {filtered.length === 0 && (
              <li style={{ color: "#888" }}>No poses found</li>
            )}
            {filtered.map(p => (
              <li
                key={p.id}
                onClick={() => onSelectPose(p)}
                style={{ cursor: "pointer", padding: "4px" }}
              >
                {p.name}
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}
