import { useEffect, useState } from "react";
import axios from "axios";
import PoseFilter from "../components/PoseFilter";
import PoseImage from "../components/PoseImage";

export default function Poses() {
  const [poses, setPoses] = useState([]);
  const [filteredPoses, setFilteredPoses] = useState([]);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/yogaposes/")
      .then((res) => {
        setPoses(res.data);
        setFilteredPoses(res.data);
      })
      .catch((err) => console.error(err));
  }, []);

  return (
    <div>
      <h2>Yoga Poses</h2>

      <PoseFilter poses={poses} setFilteredPoses={setFilteredPoses} />

      <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        {filteredPoses.map((pose) => (
          <div key={pose.id}>
            <h4>{pose.name}</h4>
            <PoseImage src={pose.image} alt={pose.name} />
          </div>
        ))}
      </div>
    </div>
  );
}
