import React, { useEffect, useState } from "react";
import axios from "axios";

const Poses = () => {
  const [categories, setCategories] = useState([]);
  const [poses, setPoses] = useState([]);

  const [selectedCategory, setSelectedCategory] = useState("");
  const [filteredPoses, setFilteredPoses] = useState([]);

  const [selectedPoseId, setSelectedPoseId] = useState("");
  const [selectedPose, setSelectedPose] = useState(null);

  /* --------------------------------------------------
     LOAD DATA ON PAGE LOAD
  -------------------------------------------------- */
  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/categories/")
      .then((res) => setCategories(res.data))
      .catch((err) => console.error("Category load error", err));

    axios
      .get("http://127.0.0.1:8000/api/yoga-poses/")
      .then((res) => setPoses(res.data))
      .catch((err) => console.error("Pose load error", err));
  }, []);

  /* --------------------------------------------------
     CATEGORY CHANGE
  -------------------------------------------------- */
  const handleCategoryChange = (e) => {
    const categoryId = e.target.value;

    setSelectedCategory(categoryId);
    setSelectedPoseId("");
    setSelectedPose(null);

    if (!categoryId) {
      setFilteredPoses([]);
      return;
    }

    const filtered = poses.filter(
      (pose) => pose.category.id === parseInt(categoryId)
    );

    setFilteredPoses(filtered);
  };

  /* --------------------------------------------------
     POSE CHANGE
  -------------------------------------------------- */
  const handlePoseChange = (e) => {
    const poseId = e.target.value;
    setSelectedPoseId(poseId);

    const pose = filteredPoses.find(
      (p) => p.id === parseInt(poseId)
    );

    setSelectedPose(pose || null);
  };

  /* --------------------------------------------------
     RENDER
  -------------------------------------------------- */
  return (
    <div style={{ padding: "20px" }}>
      <h2>Yoga Poses</h2>

      {/* CATEGORY SELECT */}
      <div>
        <label><strong>Category:</strong></label><br />
        <select
          value={selectedCategory}
          onChange={handleCategoryChange}
        >
          <option value="">-- Select Category --</option>
          {categories.map((cat) => (
            <option key={cat.id} value={cat.id}>
              {cat.name}
            </option>
          ))}
        </select>
      </div>

      {/* POSE SELECT */}
      {selectedCategory && (
        <div style={{ marginTop: "15px" }}>
          <label><strong>Pose:</strong></label><br />
          <select
            value={selectedPoseId}
            onChange={handlePoseChange}
          >
            <option value="">-- Select Pose --</option>
            {filteredPoses.map((pose) => (
              <option key={pose.id} value={pose.id}>
                {pose.pose_name}
              </option>
            ))}
          </select>
        </div>
      )}

      {/* POSE DETAILS */}
      {selectedPose && (
        <div style={{ marginTop: "25px" }}>
          <h3>{selectedPose.pose_name}</h3>
          <p>
            <strong>Category:</strong>{" "}
            {selectedPose.category.name}
          </p>

          {selectedPose.image && (
            <img
              src={selectedPose.image}
              alt={selectedPose.pose_name}
              style={{ maxWidth: "300px", border: "1px solid #ccc" }}
            />
          )}
        </div>
      )}
    </div>
  );
};

export default Poses;
