import React, { useEffect, useState } from "react";
import axios from "axios";

export default function PoseFilter({
  title = "Yoga Poses",
  poseApiUrl,
  categoryApiUrl,
  onPoseSelect = () => {}
}) {
  const [categories, setCategories] = useState([]);
  const [poses, setPoses] = useState([]);
  const [categoryId, setCategoryId] = useState("");
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);
  const [count, setCount] = useState(0);

  /* Load Categories */
  useEffect(() => {
    axios.get(categoryApiUrl)
      .then(res => setCategories(res.data))
      .catch(err => console.error(err));
  }, [categoryApiUrl]);

  /* Load Poses with Pagination */
  useEffect(() => {
    axios.get(poseApiUrl, {
      params: {
        category_id: categoryId,
        search: search,
        page: page
      }
    })
    .then(res => {
      setPoses(res.data.results);
      setCount(res.data.count);
    })
    .catch(err => console.error(err));
  }, [poseApiUrl, categoryId, search, page]);

  const totalPages = Math.ceil(count / 10);

  return (
    <div>
      <h2>{title}</h2>

      {/* Category Combo */}
      <select
        value={categoryId}
        onChange={e => {
          setCategoryId(e.target.value);
          setPage(1);
        }}
      >
        <option value="">-- Select Category --</option>
        {categories.map(cat => (
          <option key={cat.id} value={cat.id}>
            {cat.name}
          </option>
        ))}
      </select>

      {/* Search */}
      <input
        type="text"
        placeholder="Search pose..."
        value={search}
        onChange={e => {
          setSearch(e.target.value);
          setPage(1);
        }}
      />

      {/* Pose List */}
      <ul>
        {poses.map(pose => (
          <li
            key={pose.id}
            onClick={() => onPoseSelect(pose)}
            style={{ cursor: "pointer" }}
          >
            <b>{pose.name}</b> â€” {pose.category_name}
          </li>
        ))}
      </ul>

      {poses.length === 0 && <p>No poses found</p>}

      {/* Pagination Controls */}
      {totalPages > 1 && (
        <div style={{ marginTop: "10px" }}>
          <button
            disabled={page === 1}
            onClick={() => setPage(page - 1)}
          >
            Prev
          </button>

          <span style={{ margin: "0 10px" }}>
            Page {page} of {totalPages}
          </span>

          <button
            disabled={page === totalPages}
            onClick={() => setPage(page + 1)}
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
}
