import React, { useState, useEffect } from "react";

export default function PoseFilter({ categories, posesByCategory, onSelectPose }) {
  const [category, setCategory] = useState("");
  const [search, setSearch] = useState("");
  const [poses, setPoses] = useState([]);

  useEffect(() => {
    if (category) {
      setPoses(posesByCategory[category] || []);
    } else {
      setPoses([]);
    }
    setSearch("");
  }, [category, posesByCategory]);

  const filtered = poses.filter(p =>
    p.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <label>Category</label>
      <select value={category} onChange={e => setCategory(e.target.value)}>
        <option value="">--Select--</option>
        {categories.map(c => (
          <option key={c} value={c}>{c}</option>
        ))}
      </select>

      {category && (
        <>
          <label>Search Pose</label>
          <input
            type="text"
            placeholder="Type to filter pose..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />

          <ul style={{ maxHeight: "150px", overflowY: "auto", border: "1px solid #ccc" }}>
            {filtered.map(p => (
              <li key={p.id} onClick={() => onSelectPose(p)} style={{ cursor: "pointer" }}>
                {p.name}
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}
