import React, { useEffect, useState } from "react";
import axios from "axios";
import PoseSelector from "./PoseSelector";

const Poses = () => {
  const [poses, setPoses] = useState([]);
  const [selectedPose, setSelectedPose] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:8000/api/poses/")
      .then(res => setPoses(res.data))
      .catch(err => console.error(err));
  }, []);

  // ✅ THIS FUNCTION WAS MISSING OR NOT PASSED EARLIER
  const handlePoseSelect = (pose) => {
    setSelectedPose(pose);
  };

  return (
    <div>
      <h2>Select Yoga Pose</h2>

      {/* ✅ function passed correctly */}
      <PoseSelector
        poses={poses}
        onPoseSelect={handlePoseSelect}
      />

      {selectedPose && (
        <div style={{ marginTop: "20px" }}>
          <h3>{selectedPose.name}</h3>
          <p>Category: {selectedPose.category}</p>

          <img
            src={`/images/${selectedPose.image}`}
            alt={selectedPose.name}
            width="300"
          />
        </div>
      )}
    </div>
  );
};

export default Poses;
