import React, { useEffect, useState } from "react";
import axios from "axios";
import PoseSelector from "./PoseSelector";
import PoseImage from "./PoseImage";
import "./PoseImage.css";

const API_BASE = "http://127.0.0.1:8000/api";

function Poses() {
  const [categories, setCategories] = useState([]);
  const [poses, setPoses] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedPose, setSelectedPose] = useState(null);

  // ðŸ”¹ Load categories
  useEffect(() => {
    axios
      .get(`${API_BASE}/categories/`)
      .then((res) => {
        const data = Array.isArray(res.data) ? res.data : res.data.results;
        setCategories(data || []);
      })
      .catch((err) => console.error(err));
  }, []);

  // ðŸ”¹ Load poses for selected category
  useEffect(() => {
    if (!selectedCategory) {
      setPoses([]);
      setSelectedPose(null);
      return;
    }

    axios
      .get(`${API_BASE}/poses/?category=${selectedCategory}`)
      .then((res) => {
        const data = Array.isArray(res.data) ? res.data : res.data.results;
        setPoses(data || []);
        setSelectedPose(null);
      })
      .catch((err) => console.error(err));
  }, [selectedCategory]);

  return (
    <div>
      <h2>Select Category</h2>

      {/* âœ… Category dropdown */}
      <select
        value={selectedCategory}
        onChange={(e) => setSelectedCategory(e.target.value)}
      >
        <option value="">-- Select Category --</option>
        {categories.map((cat) => (
          <option key={cat.id} value={cat.id}>
            {cat.name}
          </option>
        ))}
      </select>

      {/* âœ… Pose selector */}
      {poses.length > 0 && (
        <>
          <h4 style={{ marginTop: "20px" }}>Select Pose</h4>
          <PoseSelector poses={poses} onPoseSelect={setSelectedPose} />
        </>
      )}

      {/* âœ… Pose display using shared image logic */}
      {selectedPose && (
        <div className="pose-container" style={{ marginTop: "20px" }}>
          <div className="pose-card">
            <h3>{selectedPose.name}</h3>

            <PoseImage
              src={selectedPose.image_url}
              alt={selectedPose.name}
              width="300px"
            />

            <p>
              <strong>Description:</strong> {selectedPose.description}
            </p>

            <p>
              <strong>Benefits:</strong> {selectedPose.benefits}
            </p>
          </div>
        </div>
      )}
    </div>
  );
}

export default Poses;
