import React, {useEffect, useState} from 'react';
import axios from 'axios';

export default function Poses() {
  const [poses, setPoses] = useState([]);
  useEffect(() => {
    // If your React dev server proxies /api to Django, this is fine.
    // Otherwise, change baseURL to http://localhost:8000
    axios.get('/api/poses/')
      .then(res => setPoses(res.data))
      .catch((err) => {
        console.error('Error fetching poses:', err);
        setPoses([]);
      });
  }, []);
  return (
    <div className="page">
      <h1>Yoga Poses</h1>
      {poses.length === 0 ? <p>No poses yet â€” start the backend.</p> : poses.map(p => (
        <article key={p.id} className="pose">
          <h3>{p.name}</h3>
          <p><strong>Category:</strong> {p.category}</p>
          <p>{p.description}</p>
          <p><em>Benefits:</em> {p.benefits}</p>
        </article>
      ))}
    </div>
  );
}
