import React from "react";
import Select from "react-select";

const PoseSelector = ({ poses, onPoseSelect }) => {

  // ✅ safety check (prevents crash)
  if (typeof onPoseSelect !== "function") {
    console.error("onPoseSelect is not a function");
    return null;
  }

  const options = poses.map(pose => ({
    value: pose.id,
    label: pose.name,
    pose: pose
  }));

  return (
    <Select
      options={options}
      placeholder="Type to search yoga pose..."
      onChange={(option) => onPoseSelect(option.pose)}
      isClearable
    />
  );
};

export default PoseSelector;
