import React, { useEffect, useState, useRef } from "react";
import "./Home.css";
import profileImg from "../assets/rajan.jpg";
import omSound from "../assets/om.mp3";
const poses = [
  {
    name: "Pranamasana",
    path: "M50 15 L50 55 M35 30 L50 35 L65 30 M50 55 L40 90 M50 55 L60 90"
  },
  {
    name: "Hasta Uttanasana",
    path: "M50 20 L50 55 M50 20 L30 5 M50 20 L70 5 M50 55 L45 95 M50 55 L55 95"
  },
  {
    name: "Padahastasana",
    path: "M50 25 Q50 55 50 80 M50 80 L45 95 M50 80 L55 95"
  },
  {
    name: "Ashwa Sanchalanasana",
    path: "M50 25 L50 55 M50 55 L30 95 M50 55 L70 70"
  },
  {
    name: "Dandasana (Plank)",
    path: "M20 60 L80 60 M20 60 L15 65 M80 60 L85 65"
  },
  {
    name: "Ashtanga Namaskara",
    path: "M25 65 L75 65 M40 70 L40 85 M60 70 L60 85"
  },
  {
    name: "Bhujangasana",
    path: "M30 75 Q50 35 70 75 M30 75 L25 85 M70 75 L75 85"
  },
  {
    name: "Adho Mukha Svanasana",
    path: "M30 40 L50 80 L70 40 M30 40 L25 45 M70 40 L75 45"
  },
  {
    name: "Ashwa Sanchalanasana",
    path: "M50 25 L50 55 M50 55 L70 95 M50 55 L30 70"
  },
  {
    name: "Padahastasana",
    path: "M50 25 Q50 55 50 80 M50 80 L45 95 M50 80 L55 95"
  },
  {
    name: "Hasta Uttanasana",
    path: "M50 20 L50 55 M50 20 L30 5 M50 20 L70 5"
  },
  {
    name: "Pranamasana",
    path: "M50 15 L50 55 M35 30 L50 35 L65 30"
  }
];

const Home = () => {
  const [step, setStep] = useState(0);
  const audioRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      setStep((s) => (s + 1) % poses.length);
    }, 1600);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    setTimeout(() => {
      if (audioRef.current) {
        audioRef.current.volume = 0.25;
        audioRef.current.play().catch(() => {});
      }
    }, 1200);
  }, []);

  return (
    <section className="home-section">
      <audio ref={audioRef} src={omSound} preload="auto" />

      <div className="home-wrapper">

        {/* LEFT COLUMN */}
        <div className="home-left">

          {/* ROYAL LABEL */}
          <div className="surya-title">
            Surya Namaskara <span>(Sun Salutation)</span>
            <div className="surya-subtitle">King of Yogasanas</div>
          </div>

          {/* SURYA NAMASKAR ANIMATION */}
          <div className="surya-box">
            <svg viewBox="0 0 100 100" className="asana-svg">
              <path
                d={poses[step].path}
                stroke="#1b5e20"
                strokeWidth="4"
                fill="none"
                strokeLinecap="round"
              />
            </svg>
            <div className="asana-name">{poses[step].name}</div>
          </div>

          {/* PROFILE PHOTO */}
          <img
            src={profileImg}
            alt="Rajendrakumar R. Nair"
            className="profile-photo"
          />
        </div>

        {/* RIGHT COLUMN */}
        <div className="home-text">
          <h1>Rajendrakumar R. Nair</h1>
          <h2>BSc Chemistry · MSc Yoga Therapy</h2>

          <p className="experience">
            40+ Years of Experience in Traditional Hatha Yoga & Meditation
          </p>

          <p>
            YogaPlanet is a global initiative dedicated to spreading the
            timeless wisdom of Yoga worldwide, integrating ancient yogic
            science with modern technology.
          </p>

          <p className="quote">
            “Let Yoga unite humanity. Let peace begin within.”
          </p>
        </div>

      </div>
    </section>
  );
};

export default Home;
