import "../../components/Home.css";
import { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {

  const [ads, setAds] = useState([]);
  const [index, setIndex] = useState(0);

  // Fetch ads
  useEffect(() => {
    axios.get("http://127.0.0.1:8000/api/ads/")
      .then(res => setAds(res.data))
      .catch(err => console.error("Ad fetch error:", err));
  }, []);

  // Rotate ads
  useEffect(() => {
    if (ads.length === 0) return;

    const timer = setTimeout(() => {
      setIndex((prev) => (prev + 1) % ads.length);
    }, (ads[index]?.display_seconds || 30) * 1000);

    return () => clearTimeout(timer);
  }, [ads, index]);

  return (
    <div className="home-container">

      {/* ADVERTISEMENT SECTION */}
      <div className="ad-carousel">{ads.map((ad) => {
  const file = ad.file || "";

  if (file.endsWith(".jpg") || file.endsWith(".png")) {
    return <img key={ad.id} src={file} alt={ad.name} />;
  }

  if (file.endsWith(".pdf")) {
    return <iframe key={ad.id} src={file} title={ad.name} />;
  }

  return null;
})}   </div>

      {/* HERO SECTION */}
      <section className="home-hero">
        <h1 className="home-title">
          Welcome to <span>YogaPlanet</span>
        </h1>
        <p className="home-subtitle">
          Step into the sacred science of self-realization
        </p>
      </section>

      {/* Keep your other sections here unchanged */}

    </div>
  );
};

export default Home;
