import "../../components/Home.css";
import { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
  const [ads, setAds] = useState([]);
  const [index, setIndex] = useState(0);

  // Fetch ads from backend
  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/ads/")
      .then((res) => {
        // If paginated API
        if (res.data.results) {
          setAds(res.data.results);
        } else {
          setAds(res.data);
        }
      })
      .catch((err) => console.error("Ad fetch error:", err));
  }, []);

  // Rotate ads automatically
  useEffect(() => {
    if (!ads.length) return;

    const currentAd = ads[index];
    const seconds = currentAd?.display_seconds || 30;

    const timer = setTimeout(() => {
      setIndex((prev) => (prev + 1) % ads.length);
    }, seconds * 1000);

    return () => clearTimeout(timer);
  }, [ads, index]);

  const currentAd = ads[index];

  return (
    <div className="home-container">

      {/* ================= ADVERTISEMENT SECTION ================= */}
      <div className="ad-carousel">
        {currentAd?.file && (
          <>
            {currentAd.file.toLowerCase().endsWith(".pdf") ? (
              <iframe
                src={currentAd.file}
                title={currentAd.name}
                width="100%"
                height="400px"
              />
            ) : (
              <img
                src={currentAd.file}
                alt={currentAd.name}
                className="ad-image"
              />
            )}
          </>
        )}
      </div>

      {/* ================= HERO SECTION ================= */}
      <section className="home-hero">
        <h1 className="home-title">
          Welcome to <span>YogaPlanet</span>
        </h1>
        <p className="home-subtitle">
          Step into the sacred science of self-realization
        </p>
      </section>

    </div>
  );
};

export default Home;
