import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Poses() {
  const [poses, setPoses] = useState([]);

  const categoryId = 1; // yoga_yogacategory.id for "asana"

  useEffect(() => {
    axios.get("http://127.0.0.1:8000/api/poses/", {
      params: { category_id: categoryId }
    })
    .then(res => {
      console.log("API DATA:", res.data);
      setPoses(res.data);
    })
    .catch(err => console.error(err));
  }, []);

  return (
    <div>
      <h2>Yoga Poses</h2>

      {poses.length === 0 && <p>No poses found</p>}

      <ul>
        {poses.map(pose => (
          <li key={pose.id}>
            <b>{pose.name}</b> â€“ {pose.category_name}
          </li>
        ))}
      </ul>
    </div>
  );
}
