import React, { useEffect, useState } from "react";
import axios from "axios";
import PoseSelector from "./PoseSelector";

const API_BASE = "http://127.0.0.1:8000/api";

function Poses() {
  const [categories, setCategories] = useState([]);
  const [poses, setPoses] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedPose, setSelectedPose] = useState(null);

  // ðŸ”¹ Load categories
  useEffect(() => {
    axios.get(`${API_BASE}/categories/`)
      .then(res => {
        const data = Array.isArray(res.data) ? res.data : res.data.results;
        setCategories(data || []);
      })
      .catch(err => console.error(err));
  }, []);

  // ðŸ”¹ Load poses of selected category
  useEffect(() => {
    if (!selectedCategory) {
      setPoses([]);
      setSelectedPose(null);
      return;
    }

    axios.get(`${API_BASE}/poses/?category=${selectedCategory}`)
      .then(res => {
        const data = Array.isArray(res.data) ? res.data : res.data.results;
        setPoses(data || []);
        setSelectedPose(null); // reset pose when category changes
      })
      .catch(err => console.error(err));
  }, [selectedCategory]);

  return (
    <div style={{ padding: "20px", maxWidth: "600px" }}>
      <h2>Yoga Poses</h2>

      {/* âœ… Category dropdown */}
      <select
        value={selectedCategory}
        onChange={(e) => setSelectedCategory(e.target.value)}
      >
        <option value="">-- Select Category --</option>
        {categories.map(cat => (
          <option key={cat.id} value={cat.id}>
            {cat.name}
          </option>
        ))}
      </select>

      {/* âœ… Pose searchable combo box */}
      {poses.length > 0 && (
        <>
          <h4 style={{ marginTop: "20px" }}>Select Pose</h4>
          <PoseSelector
            poses={poses}
            onPoseSelect={setSelectedPose}
          />
        </>
      )}

      <hr />

      {/* âœ… Single pose display */}
      {selectedPose && (
        <div style={{ marginTop: "20px" }}>
          <h3>{selectedPose.name}</h3>
          <p><strong>Category:</strong> {selectedPose.category_name}</p>

          {selectedPose.image_url && (
            <img
              src={selectedPose.image_url}
              alt={selectedPose.name}
              style={{ width: "300px", display: "block", marginTop: "10px" }}
            />
          )}
        </div>
      )}
    </div>
  );
}

export default Poses;
