import React from "react";
import { motion } from "framer-motion";

const Lineage = () => {
  return (
    <section className="min-h-screen flex items-center justify-center bg-gradient-to-br from-orange-50 via-yellow-50 to-orange-100 p-6">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        whileHover={{ rotateX: 6, rotateY: -6, scale: 1.02 }}
        className="max-w-4xl w-full rounded-3xl bg-white/70 backdrop-blur-xl shadow-[0_30px_80px_rgba(0,0,0,0.25)] p-8 md:p-12 relative overflow-hidden transform-gpu"
        style={{ perspective: 1200 }}
      >
        {/* Lotus background */}
        <svg
          viewBox="0 0 500 500"
          className="absolute inset-0 w-full h-full opacity-10"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M250 60 C210 130 150 170 80 190 C150 210 210 250 250 320 C290 250 350 210 420 190 C350 170 290 130 250 60 Z"
            fill="#F59E0B"
          />
          <circle cx="250" cy="210" r="40" fill="#FBBF24" />
        </svg>

        {/* Saffron glow orbs */}
        <div className="absolute -top-24 -right-24 w-72 h-72 bg-orange-400/30 rounded-full blur-3xl" />
        <div className="absolute -bottom-24 -left-24 w-72 h-72 bg-yellow-400/30 rounded-full blur-3xl" />

        <div className="relative z-10 text-center">
          <h1 className="text-3xl md:text-5xl font-extrabold tracking-wide text-transparent bg-clip-text bg-gradient-to-r from-orange-600 via-yellow-500 to-orange-700 drop-shadow-lg">
            üïâÔ∏è Founder
          </h1>

          <p className="mt-6 text-base md:text-lg leading-relaxed text-gray-800 font-medium">
            Rooted in the ancient yogic tradition of India, the Founder of <span className="font-semibold text-orange-700">YogaPlanet</span> carries a living lineage received from his great-grandfather <span className="font-semibold">Dr. B. Padmanabha Pillai (Guruji)</span> and nurtured through <span className="font-semibold">Swami RajaKrishna</span>. A lifelong yogic practitioner and teacher with over <span className="font-semibold">four decades of experience</span>, his path integrates Ha·π≠ha, RƒÅja, Karma, and Bhakti Yoga.
          </p>

          <p className="mt-4 text-base md:text-lg leading-relaxed text-gray-800 font-medium">
            Alongside yoga, he has served as a <span className="font-semibold">Chemistry teacher in Africa</span> and a <span className="font-semibold">Software Architect</span> with a Mumbai-based pharmaceutical manufacturing group. Blending spiritual wisdom, scientific discipline, and technology, he is also the <span className="font-semibold text-orange-700">Architect and Developer of YogaPlanet</span>‚Äîa space where classical yoga meets the modern world.
          </p>

          <motion.div
            whileHover={{ scale: 1.08 }}
            className="mt-8 inline-block px-6 py-3 rounded-full bg-gradient-to-r from-orange-500 to-yellow-500 text-white font-semibold tracking-wide shadow-xl"
          >
            Ancient Lineage ¬∑ Modern Vision ¬∑ Living Yoga
          </motion.div>
        </div>
      </motion.div>
    </section>
  );
};

export default Lineage;
