import React, { useEffect, useState } from "react";
import axios from "axios";

function Poses() {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [poses, setPoses] = useState([]);
  const [selectedPose, setSelectedPose] = useState(null);

  // Load categories
  useEffect(() => {
    axios.get("http://127.0.0.1:8000/api/categories/")
      .then(res => {
        // DRF safe
        setCategories(Array.isArray(res.data) ? res.data : res.data.results);
      })
      .catch(err => console.error(err));
  }, []);

  // Load poses when category changes
  useEffect(() => {
    if (!selectedCategory) {
      setPoses([]);
      return;
    }

    axios.get(`http://127.0.0.1:8000/api/poses/?category=${selectedCategory}`)
      .then(res => {
        // ðŸ”¥ THIS IS THE CRITICAL FIX
        setPoses(Array.isArray(res.data) ? res.data : res.data.results);
      })
      .catch(err => console.error(err));
  }, [selectedCategory]);

  return (
    <div>
      <h2>Yoga Poses</h2>

      {/* Category dropdown */}
      <select
        value={selectedCategory}
        onChange={e => {
          setSelectedCategory(e.target.value);
          setSelectedPose(null);
        }}
      >
        <option value="">Select Category</option>
        {categories.map(cat => (
          <option key={cat.id} value={cat.id}>
            {cat.name}
          </option>
        ))}
      </select>

      {/* Pose dropdown */}
      {poses.length > 0 && (
        <select
          onChange={e => {
            const pose = poses.find(p => p.id === parseInt(e.target.value));
            setSelectedPose(pose);
          }}
        >
          <option value="">Select Pose</option>
          {poses.map(pose => (
            <option key={pose.id} value={pose.id}>
              {pose.name}
            </option>
          ))}
        </select>
      )}

      {/* Pose details */}
      {selectedPose && (
        <div>
          <h3>{selectedPose.name}</h3>
          <p>Category: {selectedPose.category_name}</p>
          {selectedPose.image && (
            <img
              src={selectedPose.image}
              alt={selectedPose.name}
              width="300"
            />
          )}
        </div>
      )}
    </div>
  );
}

export default Poses;
