import React, { useEffect, useState } from "react";
import Select from "react-select";
import PoseImage from "../components/PoseImage";


const Poses = () => {
  const [categories, setCategories] = useState([]);
  const [poses, setPoses] = useState([]);

  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedPose, setSelectedPose] = useState(null);

  /* ---------------- LOAD CATEGORIES ---------------- */
  useEffect(() => {
    fetch("http://localhost:8000/api/categories/")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setCategories(data);
        } else if (data.results) {
          setCategories(data.results);
        } else {
          setCategories([]);
        }
      })
      .catch((err) => console.error("Category API error:", err));
  }, []);

  /* -------- LOAD POSES WHEN CATEGORY CHANGES -------- */
  useEffect(() => {
    if (!selectedCategory) {
      setPoses([]);
      setSelectedPose(null);
      return;
    }

    fetch(
      `http://localhost:8000/api/poses/?category=${selectedCategory.value}`
    )
      .then((res) => res.json())
      .then((data) => {
        console.log("POSE API RESPONSE:", data);

        if (Array.isArray(data)) {
          setPoses(data);
        } else if (data.results) {
          setPoses(data.results);
        } else {
          setPoses([]);
        }
      })
      .catch((err) => console.error("Pose API error:", err));
  }, [selectedCategory]);

  /* ---------------- SELECT OPTIONS ---------------- */
  const categoryOptions = categories.map((cat) => ({
    value: cat.id,
    label: cat.name,
  }));

  const poseOptions = poses.map((pose) => ({
    value: pose.id,
    label: pose.name,
    pose: pose,
  }));

  /* ---------------- HANDLERS (NULL SAFE) ---------------- */
  const handleCategoryChange = (option) => {
    setSelectedCategory(option);
    setSelectedPose(null);
  };

  const handlePoseChange = (option) => {
    setSelectedPose(option ? option.pose : null);
  };

  /* ---------------- UI ---------------- */
  return (
    <div style={{ maxWidth: "520px", margin: "auto" }}>
      <h2>Yoga Poses</h2>

      {/* CATEGORY */}
      <label>Category</label>
      <Select
        options={categoryOptions}
        value={selectedCategory}
        onChange={handleCategoryChange}
        isClearable
        placeholder="Select category..."
      />

      {/* POSES */}
      <label style={{ marginTop: "15px", display: "block" }}>
        Pose
      </label>
      <Select
        options={poseOptions}
        onChange={handlePoseChange}
        isClearable
        placeholder={
          poseOptions.length === 0
            ? "No poses available"
            : "Type to search pose..."
        }
        isDisabled={!selectedCategory}
      />

      {/* DETAILS */}
      {selectedPose && (
        <div style={{ marginTop: "20px" }}>
          <h3>{selectedPose.name}</h3>
          <p>Category: {selectedPose.category_name}</p>

          <PoseImage
            image={selectedPose.image}
            name={selectedPose.name}
          />
        </div>
      )}
    </div>
  );
};

export default Poses;
