import { useState } from "react";
import PoseFilter from "../components/PoseFilter";

export default function Poses() {
  const [pose, setPose] = useState(null);

  const loadPose = (poseId) => {
    fetch(`http://localhost:8000/api/pose/${poseId}/`)
      .then(res => res.json())
      .then(setPose);
  };

  return (
    <div>
      <h1>Yoga Poses</h1>
      <PoseFilter onPoseSelect={loadPose} />

      {pose && (
        <div>
          <h2>{pose.name}</h2>
          <p>Category: {pose.category_name}</p>
          <img src={pose.image} alt={pose.name} width="300"/>
        </div>
      )}
    </div>
  );
}
