import React, { useEffect, useState, useRef } from "react";
import "./Home.css";
import profileImg from "../assets/rajan.jpg";
import omSound from "../assets/om.mp3";

const poses = [
  "Pranamasana",
  "Hasta Uttanasana",
  "Padahastasana",
  "Ashwa Sanchalanasana",
  "Dandasana",
  "Ashtanga Namaskara",
  "Bhujangasana",
  "Adho Mukha Svanasana",
  "Ashwa Sanchalanasana",
  "Padahastasana",
  "Hasta Uttanasana",
  "Pranamasana"
];

const Home = () => {
  const [step, setStep] = useState(0);
  const audioRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      setStep((prev) => (prev + 1) % poses.length);
    }, 1800);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    setTimeout(() => {
      if (audioRef.current) {
        audioRef.current.volume = 0.3;
        audioRef.current.play().catch(() => {});
      }
    }, 1200);
  }, []);

  return (
    <section className="home-section">
      <audio ref={audioRef} src={omSound} preload="auto" />

      <div className="home-wrapper">

        {/* LEFT */}
        <div className="home-left">

          {/* SURYA NAMASKAR DISPLAY */}
          <div className="surya-box">
            <div className={`asana-figure step-${step}`} />
            <div className="asana-label">{poses[step]}</div>
          </div>

          <img
            src={profileImg}
            alt="Rajendrakumar R. Nair"
            className="profile-photo"
          />
        </div>

        {/* RIGHT */}
        <div className="home-text">
          <h1>Rajendrakumar R. Nair</h1>
          <h2>BSc Chemistry · MSc Yoga Therapy</h2>

          <p className="experience">
            40+ Years of Experience in Traditional Hatha Yoga & Meditation
          </p>

          <p>
            YogaPlanet is a global initiative to spread the timeless wisdom of
            Yoga worldwide, blending tradition with technology.
          </p>

          <p className="quote">
            “Let Yoga unite humanity. Let peace begin within.”
          </p>
        </div>

      </div>
    </section>
  );
};

export default Home;
