import React, { useState } from "react";
import yogaPoses from "./Poses"; // your existing poses data

function Poses() {
  const categories = [...new Set(yogaPoses.map(p => p.category))];

  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchText, setSearchText] = useState("");
  const [selectedPose, setSelectedPose] = useState(null);

  const posesInCategory = selectedCategory
    ? yogaPoses.filter(p => p.category === selectedCategory)
    : [];

  const filteredPoses = posesInCategory.filter(p =>
    p.name.toLowerCase().includes(searchText.toLowerCase())
  );

  return (
    <div>
      <h2>Yoga Poses</h2>

      {/* CATEGORY SELECT */}
      <label>Category</label><br />
      <select
        value={selectedCategory}
        onChange={e => {
          setSelectedCategory(e.target.value);
          setSearchText("");
          setSelectedPose(null);
        }}
      >
        <option value="">-- Select Category --</option>
        {categories.map(cat => (
          <option key={cat} value={cat}>{cat}</option>
        ))}
      </select>

      {/* AJAX COMBO BOX */}
      {selectedCategory && (
        <>
          <br /><br />
          <label>Search Pose</label><br />
          <input
            type="text"
            placeholder="Type to search pose..."
            value={searchText}
            onChange={e => setSearchText(e.target.value)}
            style={{ width: "250px" }}
          />

          <ul
            style={{
              border: "1px solid #ccc",
              maxHeight: "150px",
              overflowY: "auto",
              padding: "5px",
              listStyle: "none"
            }}
          >
            {filteredPoses.length === 0 && (
              <li style={{ color: "#888" }}>No poses found</li>
            )}

            {filteredPoses.map(pose => (
              <li
                key={pose.id}
                onClick={() => setSelectedPose(pose)}
                style={{ cursor: "pointer", padding: "4px" }}
              >
                {pose.name}
              </li>
            ))}
          </ul>
        </>
      )}

      {/* POSE DETAILS */}
      {selectedPose && (
        <div style={{ marginTop: "20px" }}>
          <h3>{selectedPose.name}</h3>
          <p>{selectedPose.category}</p>
          <img
            src={`/images/${selectedPose.image}`}
            alt={selectedPose.name}
            width="300"
          />
        </div>
      )}
    </div>
  );
}

export default Poses;
