import React, { useEffect, useState, useRef } from "react";
import "./Home.css";
import profileImg from "../assets/rajan.jpg";
import omSound from "../assets/om.mp3";

const poses = [
  { name: "Pranamasana", path: "M50 20 L50 60 M35 35 L65 35 M50 60 L40 90 M50 60 L60 90" },
  { name: "Hasta Uttanasana", path: "M50 20 L50 60 M50 20 L30 5 M50 20 L70 5 M50 60 L45 95 M50 60 L55 95" },
  { name: "Padahastasana", path: "M50 30 L50 70 M50 70 L45 95 M50 70 L55 95" },
  { name: "Ashwa Sanchalanasana", path: "M50 25 L50 60 M50 60 L30 95 M50 60 L65 85" },
  { name: "Dandasana", path: "M20 60 L80 60" },
  { name: "Ashtanga Namaskara", path: "M25 65 L75 65 M50 65 L50 85" },
  { name: "Bhujangasana", path: "M30 70 Q50 40 70 70" },
  { name: "Adho Mukha Svanasana", path: "M30 40 L50 80 L70 40" },
  { name: "Ashwa Sanchalanasana", path: "M50 25 L50 60 M50 60 L30 95 M50 60 L65 85" },
  { name: "Padahastasana", path: "M50 30 L50 70 M50 70 L45 95 M50 70 L55 95" },
  { name: "Hasta Uttanasana", path: "M50 20 L50 60 M50 20 L30 5 M50 20 L70 5" },
  { name: "Pranamasana", path: "M50 20 L50 60 M35 35 L65 35" }
];

const Home = () => {
  const [step, setStep] = useState(0);
  const audioRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      setStep((s) => (s + 1) % poses.length);
    }, 1600);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    setTimeout(() => {
      if (audioRef.current) {
        audioRef.current.volume = 0.25;
        audioRef.current.play().catch(() => {});
      }
    }, 1200);
  }, []);

  return (
    <section className="home-section">
      <audio ref={audioRef} src={omSound} preload="auto" />

      <div className="home-wrapper">

        {/* LEFT COLUMN */}
        <div className="home-left">

          {/* ROYAL LABEL */}
          <div className="surya-title">
            Surya Namaskara <span>(Sun Salutation)</span>
            <div className="surya-subtitle">King of Yogasanas</div>
          </div>

          {/* SURYA NAMASKAR ANIMATION */}
          <div className="surya-box">
            <svg viewBox="0 0 100 100" className="asana-svg">
              <path
                d={poses[step].path}
                stroke="#1b5e20"
                strokeWidth="4"
                fill="none"
                strokeLinecap="round"
              />
            </svg>
            <div className="asana-name">{poses[step].name}</div>
          </div>

          {/* PROFILE PHOTO */}
          <img
            src={profileImg}
            alt="Rajendrakumar R. Nair"
            className="profile-photo"
          />
        </div>

        {/* RIGHT COLUMN */}
        <div className="home-text">
          <h1>Rajendrakumar R. Nair</h1>
          <h2>BSc Chemistry · MSc Yoga Therapy</h2>

          <p className="experience">
            40+ Years of Experience in Traditional Hatha Yoga & Meditation
          </p>

          <p>
            YogaPlanet is a global initiative dedicated to spreading the
            timeless wisdom of Yoga worldwide, integrating ancient yogic
            science with modern technology.
          </p>

          <p className="quote">
            “Let Yoga unite humanity. Let peace begin within.”
          </p>
        </div>

      </div>
    </section>
  );
};

export default Home;
