import "../../components/Home.css";
import { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
  return (
    <div className="home-container">
      {/* HERO SECTION */}
      <section className="home-hero">
        <h1 className="home-title">
          Welcome to <span>YogaPlanet</span>
        </h1>

        <p className="home-subtitle">
          Step into the sacred science of self-realization
        </p>
      </section>

      {/* INSPIRING MESSAGE */}
      <section className="home-message">
        <p>
          Yoga is not merely exercise.  
          It is a <strong>journey inward</strong> ‚Äî from restlessness to stillness,
          from confusion to clarity, from limitation to liberation.
        </p>

        <p>
          At <strong>YogaPlanet</strong>, we invite you to explore the
          <em> Ocean of Yoga </em> ‚Äî
          vast, timeless, and deeply transformative.
        </p>

        <p>
          Whether you are a beginner taking your first conscious breath,
          or a sincere seeker walking the path for years,
          this space is created to <strong>guide, inspire, and uplift</strong>.
        </p>
      </section>

      {/* QUOTE SECTION */}
      <section className="home-quote">
        <blockquote>
          ‚ÄúYoga is the journey of the self,  
          through the self,  
          to the self.‚Äù
        </blockquote>
        <cite>‚Äî Bhagavad Gƒ´tƒÅ</cite>
      </section>

      {/* INVITATION */}
      <section className="home-invite">
        <h2>Begin Your SƒÅdhana</h2>
        <p>
          Explore classical ƒÅsanas, healing therapies, pranayama,
          meditation, and yogic wisdom ‚Äî
          taught with reverence to tradition and clarity for modern life.
        </p>

        <p className="home-blessing">
          May your practice be steady.  
          May your breath be deep.  
          May your life be luminous. üïâÔ∏è
        </p>
      </section>
    </div>
  );
 const [ads, setAds] = useState([]);
  const [index, setIndex] = useState(0);

  useEffect(() => {
    axios.get("http://127.0.0.1:8000/api/ads/")
      .then(res => setAds(res.data))
      .catch(err => console.error(err));
  }, []);

  useEffect(() => {
    if (ads.length === 0) return;

    const timer = setTimeout(() => {
      setIndex((prev) => (prev + 1) % ads.length);
    }, ads[index]?.display_seconds * 1000 || 30000);

    return () => clearTimeout(timer);
  }, [ads, index]);

  return (
    <div className="home-container">

      <div className="ad-carousel">
        {ads.length > 0 && (
          ads[index].image_url.endsWith(".pdf") ? (
            <a href={ads[index].image_url} target="_blank" rel="noreferrer">
              View Advertisement PDF
            </a>
          ) : (
            <img src={ads[index].image_url} alt="Advertisement" />
          )
        )}
      </div>

      <section className="home-hero">
        <h1>Welcome to YogaPlanet</h1>
      </section>

    </div>
  );


};

export default Home;
