import React, { useEffect, useState } from "react";
import axios from "axios";
import Select from "react-select";
import PoseImage from "../components/PoseImage";

const API_BASE = "http://127.0.0.1:8000/api";

function Poses() {
  const [categories, setCategories] = useState([]);
  const [poses, setPoses] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedPose, setSelectedPose] = useState(null);

  // Load categories on page load
  useEffect(() => {
    axios.get(`${API_BASE}/categories/`).then((res) => {
      setCategories(res.data);
    });
  }, []);

  // Load poses when category changes
  useEffect(() => {
    if (selectedCategory) {
      axios
        .get(`${API_BASE}/poses/?category=${selectedCategory.value}`)
        .then((res) => {
          setPoses(res.data);
          setSelectedPose(null);
        });
    }
  }, [selectedCategory]);

  const categoryOptions = categories.map((c) => ({
    value: c.id,
    label: c.name,
  }));

  const poseOptions = poses.map((p) => ({
    value: p.id,
    label: p.name,
    pose: p,
  }));

  return (
    <div style={{ padding: "20px" }}>
      <h2>Yoga Poses</h2>

      {/* Category selector */}
      <Select
        placeholder="Select Category"
        options={categoryOptions}
        value={selectedCategory}
        onChange={setSelectedCategory}
      />

      {/* Pose selector */}
      {selectedCategory && (
        <div style={{ marginTop: "15px" }}>
          <Select
            placeholder="Select Pose"
            options={poseOptions}
            value={
              selectedPose
                ? {
                    value: selectedPose.id,
                    label: selectedPose.name,
                  }
                : null
            }
            onChange={(opt) => setSelectedPose(opt.pose)}
          />
        </div>
      )}

      {/* Pose details */}
      {selectedPose && (
        <div style={{ marginTop: "20px" }}>
          <h3>{selectedPose.name}</h3>
          <p>Category: {selectedPose.category_name}</p>
          <PoseImage image={selectedPose.image} />
        </div>
      )}
    </div>
  );
}

export default Poses;
